@property
def ci_passing(self) -> bool:
    """Check if CI is passing."""
    if not self.checks:
        return True  # No checks means no failures
    for check in self.checks:
        state = check.get("state")
        if state not in ["SUCCESS", "PENDING", "QUEUED"]:
            return False
    return True

