   return headers.map((header) => {
     const normalized = header.trim().toLowerCase();
     if (!normalized) return 'skip';

     for (const [field, aliases] of Object.entries(HEADER_ALIASES) as [Exclude<ContactField, 'skip'>, string[]][]) {
