-      if (usedFields.has(field)) continue;
+      if (field !== 'name' && usedFields.has(field)) continue;

       const hasPartialMatch = aliases.some((alias) =>
         normalized.includes(alias) || alias.includes(normalized),
       );
       if (hasPartialMatch) {
-        usedFields.add(field);
+        if (field !== 'name') {
+          usedFields.add(field);
+        }
         return field;
